from django import formsfrom .models import Promo, Partner, Itemclass DateInput(forms.DateInput):    input_type = 'date'class PromoForm(forms.ModelForm):    partner = forms.ModelChoiceField(queryset=Partner.objects.all())    items = forms.ModelMultipleChoiceField(queryset=Item.objects.all(), widget=forms.CheckboxSelectMultiple)    start_date = forms.DateField(widget=DateInput)    end_date = forms.DateField(widget=DateInput)    class Meta:        model = Promo        fields = ['title', 'start_date', 'end_date', 'partner', 'items']