from django.conf import settingsfrom django.conf.urls.static import staticfrom django.urls import pathfrom . import viewsurlpatterns = [    path('', views.home_page, name='home'),    path('login/', views.login, name='login'),    path('items/', views.items, name='items'),    path('promo/', views.promo, name='promo'),    path('<slug:category_slug>/<slug:slug>/', views.item_page, name='item_detail'),]if settings.DEBUG:    urlpatterns += static(settings.MEDIA_URL, document_root=settings.MEDIA_ROOT)